import React, { Component } from 'react'
import PropTypes from 'prop-types';

export default class Search extends Component {

    state={
        text:''
    }

    static propTypes={
        searchUsers:PropTypes.func.isRequired,
        clearUsers:PropTypes.func.isRequired,
    }




    onChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value //we have used name="text" so [e.target.name]==text
        })
    }
    onSubmit=(e)=>{
        e.preventDefault();
        this.props.searchUsers(this.state.text)
        this.setState({text:''})
    }
    render() {
        return (
            <div>
            <form onSubmit={this.onSubmit} className="form">
                <input type="text" name="text" placeholder="Search Users..." value={this.state.text} onChange={this.onChange}></input>
                <input type="submit" value="search" className="btn btn-dark btn-block"/>
            </form>
            <button onClick={this.props.clearUsers} className="btn btn-light btn-block">Clear</button>
        </div>
        )
    }
}
