import React,{Component} from 'react'

class UserItem extends Component{
    // constructor(){// a function when component runs
    //     super(); // to call the parent class construtor
    //     this.state={}
    //   }
    state={ // it is a js object
        id:'id',
        login:'mojombo',
        avatar_url:'https://avatars.githubusercontent.com/u/1?v=4',
        html_url:'https://github.com/mojombo'
      }
    render(){

        return (
            <div className="card text-center">
                <img src={this.state.avatar_url} className="round-img" alt="" style={{width:"60px"}}/>
                <h3>{this.state.login}</h3>
                <a href={this.state.html_url} className="btn btn-dark btn-sm my-1">More</a>
            </div>
        )
    }

}

export default UserItem
