import React from 'react'
import {BrowserRouter as Router,Switch,Route} from 'react-router-dom'
import './App.css';
import Navbar from '../src/components/layouts/Navbar'
import Users from '../src/components/users/Users'
import axios from 'axios'
import Search from '../src/components/users/Search'
import Alert from './components/layouts/Alert'
import About from './components/pages/About'
import User from './components/users/User'

class App extends React.Component {
  state={
    users:[],
    user:{},
    loading:false,
    alert:null
  }
 async componentDidMount(){  //it is a lifecycle method , render is also
  // set loading true
  this.setState({loading:true})

  const res = await axios.get
  (`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
  // data is ready so set loading to false and set the data
  this.setState({users:res.data,loading:false})




  }
  //search github users
  searchUsers=async (text)=>{

    this.setState({loading:true})
    const res = await axios.get
  (`https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
  // data is ready so set loading to false and set the data
  this.setState({users:res.data.items,loading:false})

  }

  //search single github user

  getUser=async(username)=>{
    this.setState({loading:true})
    const res = await axios.get
  (`https://api.github.com/users/${username}?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);
  // data is ready so set loading to false and set the data
  this.setState({user:res.data,loading:false})
  }


  clearUsers = ()=>{
    this.setState({
      users:[],
      loading:false
    })
  }

  setAlert=(msg,type)=>{
    this.setState({
      alert:{msg:msg,type:type}
    })
    setTimeout(()=>{
      this.setState({
        alert:null
      })
    },4000)
  }


  render(){

    const {users,loading} = this.state;

    return (
      <Router>
        <div className="App bgColor">
        <Navbar />
        <div className="container" style={{backgroundColor:"white"}}>
          <Alert alert={this.state.alert}/>
          <Switch>

            <Route path='/' exact render={props=>(
              <>
               <Search searchUsers={this.searchUsers} 
          clearUsers={this.clearUsers} 
          showClear={users.length>0 ? true:false}
          setAlert={this.setAlert}
          />
        <Users loading={loading} users={users}/>
              </>
            )}></Route>
          <Route exact path="/about" component={About}></Route>
          <Route path="/user/:login"  render={props=>(

            <User {...props} getUser={this.getUser} user={user} loading={loading} ></User>

          )}
          ></Route>
          </Switch>

        </div>

      </div>

      </Router>

    );
  }

}

export default App;
